mapper.sql:
WHERE 1=1

	AND frn_fornecedor.ID_FORNECEDOR = #{idFornecedor} 


	AND GBL_PESSOA.NRO_CPF_CNPJ = #{nroCpfCnpj} AND GBL_PESSOA.DIG_CPF_CNPJ = #{digCpfCnpj}


	AND GBL_PESSOA.RAZAO like #{razao}

mapper.update:
